-- ----------------------------
-- Records of mission
-- ----------------------------
INSERT INTO `mission` VALUES (1, '人员分配', '在项目开发中，团队的建立是最重要的，每个人都有自己擅长的部分，因此需要给每个人相对应的职位，从而提高整体开发效率', '进行人员分配之前需要先邀请成员，在左侧导航栏点击开发模式，通过输入学号查找对应同学发送邀请，在该同学同意后会出现在成员列表中，通过修改成员职责可以赋予其对应的职位', '众人拾柴火焰高的道理想必都知道，在邀请完成员组成一个team后，队长要起领导作用，给小组成员分配好职责，这样才能提高项目的开发效率，并将每个人的作用最大化。', 'Development.jsp');
INSERT INTO `mission` VALUES (2, '需求分析', '需求分析即作品必须完成哪些事，必须实现哪些功能，以及为了向其用户提供有用的功能所需执行的动作', '需要填写功能需求时可点击导航选择开发文档->在文档模板内填写功能需求题目和内容->点击提交即可查看添加的功能需求', '需求分析是分析系统在功能上需要“实现什么”，而不是考虑如何去“实现”。需求分析的目标是把用户对待开发软件提出的“要求”或“需要”进行分析与整理，确认后形成描述完整、清晰与规范的文档，确定软件需要实现哪些功能，完成哪些工作。在明确好需求之后可根据需求进行代码设计，亦可在后期需要添加功能时修改功能需求以便开发', 'Document.jsp');
INSERT INTO `mission` VALUES (3, '测试用例', '测试是为某个特殊目标而编制的一组测试输入、执行条件以及预期结果，以便测试某个程序路径或核实是否满足某个特定需求', '需要填写测试用例时可点击导航选择测试用例->可查看根据小组需求系统自动生成的测试用例，也可以通过点击添加测试用例添加自定义测试用例', '测试用例主要是为了将测试具体量化的一种方法，它可以提高软件的完整性，在一定程度上，不是为了找出软件的bug，而是增加程序的完整性，使其能够兼容更多情况的发生', 'TestUnit.jsp');
INSERT INTO `mission` VALUES (4, '开发文档', '开发文档是软件开发使用和维护过程中的必备资料。它能提高软件开发的效率，保证软件的质量，而且在软件的使用过程中有指导，帮助，解惑的作用，尤其在维护工作中，文档是不可或缺的资料', '需要填写开发文档时可点击导航选择开发文档->根据年级的限制选择可选择的模板即可自动生成开发文档模板->修改文档内容点击保存即可更新文档', '开发文档作为检查软件开发进度和开发质量的依据，是软件开发规范和指南，它能实现对软件开发的工程管理。根据开发文档能提高开发效率，并作为软件开发人员的一定成果和结束标志。在开发过程中可以便于软件的开发、使用和维护，以及管理人员、开发人员、操作人员、用户之间的协作', 'Document.jsp');
INSERT INTO `mission` VALUES (5, '开发交流', '开发交流是为了防止项目的统一性被破坏，在修改以及设立字段时在小组内发布的一些信息', '需要发布开发交流信息时点击导航选择开发交流->填写开发交流的内容选择提交即可查看发布的内容，需要查看小组其他成员的开发交流信息亦可点击开发交流查看', '开发交流的设立可以避免在开发过程中因为沟通交流的不便而造成的例如属性字段名填写错误，前后端版本不统一等情况，也可以在开发交流中查看测试的反馈以便修改bug', 'Communicate.jsp');
INSERT INTO `mission` VALUES (6, '查看测试', '测试是项目开发中必不可少且至关重要的一环，在《人月神话》一书中将项目开发时间分配成1:1:3的比例，测试则是占3的那一部分，可见测试的重要性。学会如何查看测试，如何填写测试，都是一个开发者必须掌握的技能。', '查看测试可点击左侧导航栏测试用例->页面会显示最新发布的测试信息，点击对应的测试可查看测试的详细内容（扩展：测试主要有测试的内容，测试的预期结果和测试结果，通过测试结果来判断是否达到预期结果）', '测试是开发中最为重要的部分之一，学会如何查看测试，如何根据测试结果来找bug和修改bug，在这样的过程中就会提高个人的编程水平，并通过测试总结以减少此类bug产生的次数', 'TestUnit.jsp');
INSERT INTO `mission` VALUES (7, '查看开发交流', '开发时候一些必要的沟通是必须的，通过交流可以让前后端更好的交互，达到前后一致，不出现沟通不当而产生的bug', '查看测试可点击左侧导航栏开发交流->页面会显示最新发布的交流信息，点击对应的交流信息可查看交流的详细内容（扩展：开发交流主要是包括对属性字段的命名，对接口的调用等前后端需一致的东西，需提前商定好再进行开发）', '交流是团队开发不可或缺的部分，学会和团队中的其他人沟通交流，将自己的一些问题准确的表达出来是一门必修课，毕竟项目开发是一个团队性的工作，只有学会如何配合才会提高效率', 'Communicate.jsp');
INSERT INTO `mission` VALUES (8, '文档导出', '文档是一个项目的总结，一份好的开发文档是可以概括这个项目的几个最重要的点，囊括团队中每个人所负责的细节。', '文档导出可点击左侧导航栏文档导出->页面会显示文档导出的按钮，点击即可将自己填写的文档导出成word文件（PS：文档导出主要导出的是选择的模板进行修改后的内容，用户亦可删除模板自行填写文档）', '文档是软件开发使用和维护过程中的必备资料。尤其是在维护过程中可以根据文档来进行整理维护，并且可以总结经验，避免相同开发问题的产生。', 'FileExport.jsp');

